create table accountability (id bigint generated by default as identity, addition_value decimal(19,2), date_payment date, discount_value decimal(19,2), observation varchar(255), total_value decimal(19,2), id_service_provided integer, id_type_payment bigint, primary key (id));
create table client (id integer generated by default as identity, cpf varchar(255) not null, date_register date, name varchar(255) not null, primary key (id));
create table role (role_id bigint generated by default as identity, name varchar(255), primary key (role_id));
create table service_provided (id integer generated by default as identity, date date, descricao varchar(255) not null, released_payment boolean, value decimal(19,2), id_client integer, id_type_service bigint, primary key (id));
create table type_payment (id bigint generated by default as identity, observation varchar(255), type varchar(255), primary key (id));
create table type_service (id bigint generated by default as identity, observation varchar(255), service varchar(255), primary key (id));
create table user_role (user_id bigint not null, role_id bigint not null, primary key (user_id, role_id));
create table users (user_id bigint generated by default as identity, created_date timestamp not null, display_name varchar(255), email varchar(255), enabled boolean, modified_date timestamp, password varchar(255), provider varchar(255), provider_user_id varchar(255), primary key (user_id));
